<template>
    <div class="attachment" v-if="files && files.length">
        <strong>{{ files.length }} Attachments</strong>
            <div class="attachment-block">
                <div class="attachment-item" v-for="file in files" :key="file" @click="download(file)">
                    <div class="attachment-item-inner">
                        <div class="attachment-thumbnail attachment-image"
                            :style="`background-image: url('${baseUrl}/thumbnail/file/${file.filepath}')`"
                            v-if="image_extension.includes(file.type)"
                        ></div>
                        <div class="attachment-thumbnail"
                            :class="`file-${file.type}`"
                            v-else
                        >
                            <div class="file-icon"></div>
                        </div>
                        <div class="attachment-filename">
                            <span>{{ file.name }}</span>
                        </div>
                        <div class="clearfix"></div>
                    </div>
                </div>
                <div class="clearfix"></div>
            </div>
        </div>
    </div>
</template>

<script>
export default {
    props: ['files'],
    data(){
        return {
            temp_image: "data:image/svg+xml;base64,",
            image_extension: ['jpeg', 'jpg', 'png', 'svg', 'gif'],
            baseUrl: this.$store.getters.baseUrl,
            token: this.$auth.getToken()
        }
    },
    methods:{
        download(file){
            axios.get(`api/attachment/download/${file.filepath}`, {
                headers: {
                    Authorization: 'Bearer ' + this.token
                }
            }).then(response => {
                window.open(response.data.download_url, "_blank")
            })
        }
    }
}
</script>
